//多边形的外型
static const GUI_POINT _aNeedle[] = {
	{ MAG * (0), MAG * (0 + 125) },
	{ MAG * (-3), MAG * (-15 + 125) },
	{ MAG * (-3), MAG * (-65 + 125) },
	{ MAG * (3), MAG * (-65 + 125) },
	{ MAG * (3), MAG * (-15 + 125) },
};

//包含位图所需信息的结构体
typedef struct {
	GUI_AUTODEV_INFO AutoDevInfo;  // Information about what has to be displayed
	GUI_POINT        aPoints[7];   // Polygon data
	float            Angle;
} PARAM;


static void _Draw(void * p) {
	PARAM * pParam;

	pParam = (PARAM *)p;
	//
	// Fixed background
	//
	if (pParam->AutoDevInfo.DrawFixed) {
		GUI_ClearRect(20, 80 + bmScaleR140.YSize, 20 + bmScaleR140.XSize - 1, 180);
		GUI_DrawBitmap(&bmScaleR140, 20, 80);
	}
	//
	// Moving needle
	//
	GUI_SetColor(GUI_WHITE);
	GUI_AA_FillPolygon(pParam->aPoints, countof(_aNeedle), MAG * 120, MAG * 220);//绘制实心无锯齿多边形

}

void fandraw(unsigned int i)
{
	GUI_AUTODEV AutoDev;
	PARAM       Param;

	GUI_MEMDEV_CreateAuto(&AutoDev); //创建自动设备对象

	Param.Angle = i* DEG2RAD;
	GUI_RotatePolygon(Param.aPoints, _aNeedle, countof(_aNeedle), Param.Angle);//Param.Angle                        
	GUI_MEMDEV_DrawAuto(&AutoDev, &Param.AutoDevInfo, &_Draw, &Param);
	
	 GUI_MEMDEV_DeleteAuto(&AutoDev);
}

void dwq_task(void) {
	unsigned int  i;
	static uint8_t value;
	static uint8_t flag = 0;
  	/* 初始化 */
	
	if(flag == 0)
	{
			GUI_Init();
			GUI_AA_EnableHiRes();//允许抗锯齿功能
			GUI_AA_SetFactor(MAG);//抗锯齿功能等级 4;
	}
	flag = 1;

		value = Dial_value_get();
		i = 135 + 0.35*value;
		fandraw(i);
		GUI_Delay(10);

}

#define BLACKCOLOR GUI_BLUE
void Traffic_light(uint8_t a,uint8_t b,uint8_t c)
{
	static uint8_t traffic_flag = 0;
	
	if(traffic_flag == 0)
	{
		GUI_Init();
		GUI_SetBkColor(GUI_WHITE);  
		GUI_Clear();
		GUI_SetColor(GUI_BLUE);
		GUI_FillRoundedRect(30, 80, 209, 160, 10);
	}
	
	traffic_flag = 1;
		if(a)	{
		GUI_SetColor(GUI_RED);
		GUI_FillCircle(70, 120, 20);
		}
		else{
			GUI_SetColor(BLACKCOLOR);
			GUI_FillCircle(70, 120, 20);
			GUI_SetColor(GUI_RED);
			GUI_DrawCircle(70, 120, 20);
		}
		
	
	if(b)	{
		GUI_SetColor(GUI_YELLOW);
		GUI_FillCircle(120,120,20);
	}
	else{
		GUI_SetColor(BLACKCOLOR);
		GUI_FillCircle(120, 120, 20);
		GUI_SetColor(GUI_YELLOW);
		GUI_DrawCircle(120, 120, 20);
	}
	
	if(c){
		GUI_SetColor(GUI_GREEN);
		GUI_FillCircle(170, 120, 20);
	}
	else{
		GUI_SetColor(BLACKCOLOR);
		GUI_FillCircle(170, 120, 20);
		GUI_SetColor(GUI_GREEN);
		GUI_DrawCircle(170, 120, 20);
	}
	
	
}

